package name.zeno.android.third.alipayimport android.text.TextUtilsimport com.alipay.sdk.app.iclass AlipayResult {  /**   * @see com.alipay.sdk.app.i alipay 错误码 enum   */  var resultStatus: Int = 0    private set  var result: String? = null    private set  var memo: String? = null    private set  constructor(rawResult: String) {    if (TextUtils.isEmpty(rawResult))      return    val resultParams = rawResult.split(";".toRegex()).dropLastWhile { it.isEmpty() }.toTypedArray()    for (resultParam in resultParams) {      if (resultParam.startsWith("resultStatus")) {        resultStatus = Integer.valueOf(gatValue(resultParam, "resultStatus"))!!        i.a(resultStatus)      }      if (resultParam.startsWith("result")) {        result = gatValue(resultParam, "result")      }      if (resultParam.startsWith("memo")) {        memo = gatValue(resultParam, "memo")      }    }  }  constructor(rawResult: Map<String, String>?) {    if (rawResult == null) {      return    }    this.resultStatus = Integer.valueOf(rawResult["resultStatus"])!!    this.result = rawResult["result"]    this.memo = rawResult["memo"]  }  override fun toString(): String {    return ("resultStatus={" + resultStatus + "};memo={" + memo        + "};result={" + result + "}")  }  private fun gatValue(content: String, key: String): String {    val prefix = key + "={"    return content.substring(content.indexOf(prefix) + prefix.length,        content.lastIndexOf("}"))  }}